package com.example.applistfruit

import android.content.Context
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import kotlin.coroutines.coroutineContext


class Adapter(private val fruit: List<Fruit>, val context: Context) : RecyclerView.Adapter<Adapter.ViewHolder>() {

    lateinit var onItemClickListener: (Fruit) -> Unit

    inner class ViewHolder(val view: View) : RecyclerView.ViewHolder(view) {
        private val tvFruta: TextView = view.findViewById(R.id.textViewFruta)
        private val imgFruta:ImageView = view.findViewById(R.id.imageViewFruta)
        private val persimmon: String = "data:image/jpeg;base64,"
        private val strawbrery: String = "https://freepngimg.com/download/temp_png/13-2-strawberry-png.jpeg"
        private val banana: String = "https://png.pngtree.com/png-clipart/20201208/ourlarge/pngtree-banana-png-png-image_2532632.jpg"
        private val tomate: String = "data:image/jpeg;base64,"
        private val pear: String = "https://images.squarespace-cdn.com/content/v1/624c46bc9a2d596197360925/bf0fa26f-b120-4c73-8c0b-f8a1c6d60d19/pears.jpg"
        private val durian: String = "https://static.wixstatic.com/media/ff8b20_9cf0f2bd5d8d4f5d8a43cbf01d784974~mv2.jpg/v1/fill/w_1000,h_730,al_c,q_85,usm_0.66_1.00_0.01/ff8b20_9cf0f2bd5d8d4f5d8a43cbf01d784974~mv2.jpg"
        private val imgGenerica: String = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQUHmCyNFyUhNqBfBpHMJ8uObHAD13l5KSW81EaxvaHbCRawYfftEp6bptezvd7210ej94&usqp=CAU"

        fun bind(fruit: Fruit) {
            tvFruta.text = fruit.name

            if ("Persimmon" == fruit.name){
                  fruit.image = persimmon
            }else if ("Strawberry" == fruit.name){
                fruit.image = strawbrery
            }else if ("Banana" == fruit.name){
                fruit.image = banana
            }else if ("Tomato" == fruit.name){
                fruit.image = tomate
            }else if ("Pear" == fruit.name){
                fruit.image = pear
            }else if ("Durian" == fruit.name){
                    fruit.image = durian
            }else{
                fruit.image = imgGenerica
            }

            Glide.with(context).load(fruit.image).into(imgFruta)
            view.setOnClickListener {
                onItemClickListener(fruit)
            }

        }
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val layoutInflater = LayoutInflater.from(parent.context)
        return ViewHolder(layoutInflater.inflate(R.layout.item_list, parent, false))
    }

    override fun getItemCount(): Int {
        return fruit.size
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val fruit = fruit[position]
        holder.bind(fruit)
    }
}